import React, { useEffect, useState } from "react";
import { Button, TextField, Switch } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import CloseIcon from "@material-ui/icons/Close";

const AddContactModal = ({
  isOpen = false,
  size = "md",
  handleClose = () => {},
  isCentered = true,
  backdrop = "static",
  isEditContact = false,
  contactData = {},
}) => {
  const [disableSegmentInput, setDisableSegmentInput] = useState(true);
  const [formData, setFormData] = useState({ ...contactData });

  useEffect(() => {
    console.log("yoooo=", formData);
  });

  const onAddContactClick = () => {};

  const onChangeField = (e, key) => {};

  return (
    <Modal
      show={isOpen}
      onHide={handleClose}
      size={size}
      backdrop={backdrop}
      centered={isCentered}
      className="contact-modal-root"
    >
      <Modal.Header>
        <div>
          <h6>{isEditContact ? "Edit Contact" : "Add Contact"}</h6>
        </div>
        <div
          style={{ float: "right", cursor: "pointer" }}
          onClick={handleClose}
        >
          <CloseIcon />
        </div>
      </Modal.Header>
      <Modal.Body>
        <div className="form-contents-cs">
          <TextField label="First Name" fullWidth value={formData.first_name} />
          <TextField label="Last Name" fullWidth value={formData.last_name} />
          <TextField label="Email Id" fullWidth value={formData.email} />
          <TextField
            label="Mobile Number"
            fullWidth
            value={formData.phone_number}
          />
          <div className="table-switch-column">
            Inactive
            <Switch
              checked={formData.status === "active"}
              onChange={() => {}}
            />
            Active
          </div>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onAddContactClick}>
          {isEditContact ? "Update Contact" : "Add Contact"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddContactModal;
