import React, {useState} from 'react';
import { 
    Table, 
    TableBody,
    TableCell, 
    TableContainer, 
    TableHead, 
    TableRow
} from '@material-ui/core';
import Paper from '@material-ui/core/Paper';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import DeleteOutlineOutlinedIcon from '@material-ui/icons/DeleteOutlineOutlined';
import { Switch } from '@material-ui/core';

const LastTableChild = ({
    id=''
}) => {
    const [menuOpen, setMenuOpen] = useState(null);

    const handleClick = (event) => {
        setMenuOpen(event.currentTarget);
      };

      const handleClose = () => {
        setMenuOpen(null);
      };

    return (
        <>
        <MoreVertIcon onClick={handleClick}></MoreVertIcon>
        <Menu
        id={`menu-${id}`}
        anchorEl={menuOpen}
        keepMounted
        open={Boolean(menuOpen)}
        onClose={handleClose}
        className="table-row-menu"
        >
            <MenuItem onClick={handleClose}>
                <EditOutlinedIcon /> Edit Contact
            </MenuItem>
            <MenuItem onClick={handleClose}>
                <DeleteOutlineOutlinedIcon /> Delete Contact
            </MenuItem>
        </Menu>
        </>
    )
}

const TableComponent = ({ tableData=[], tableHeaderData=[]}) => {

  return (
    <TableContainer component={Paper} className="table-root">
      <Table>
        <TableHead>
          <TableRow>
              { 
                tableHeaderData.map((item) => 
                 <TableCell align="center">{item}</TableCell>
                 )
              }
          </TableRow>
        </TableHead>
        <TableBody>
          {tableData.map((rowData) => (
            <TableRow key={rowData.id}>
              <TableCell component="th" scope="row" align="center">
                {rowData.first_name}
              </TableCell>
              <TableCell align="center">{rowData.last_name}</TableCell>
              <TableCell align="center">{rowData.email}</TableCell>
              <TableCell align="center">{rowData.phone_number}</TableCell>
              <TableCell align="center" className="table-switch-column">
                  Active
                  <Switch checked={rowData.status === 'active'} onChange={()=>{}} />
                  Inactive
              </TableCell>
              <TableCell align="center">
                  <div><EditOutlinedIcon /></div>
                  <div><DeleteOutlineOutlinedIcon /></div>
                  {/* <LastTableChild id={rowData.id} /> */}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}

export default TableComponent;
