import React, { useState } from "react";
import { Button, TextField, Switch } from "@material-ui/core";
import { Modal } from "react-bootstrap";
import CloseIcon from "@material-ui/icons/Close";

const AddContactModal = ({
  isOpen = false,
  size = "md",
  handleClose = () => {},
  isCentered = true,
  backdrop = "static",
  isEditContact = false,
  contactData = {},
}) => {
  const [disableSegmentInput, setDisableSegmentInput] = useState(true);

  const onAddContactClick = () => {};

  return (
    <Modal
      show={isOpen}
      onHide={handleClose}
      size={size}
      backdrop={backdrop}
      centered={isCentered}
      className="contact-modal-root"
    >
      <Modal.Header>
        <div>
          <h6>{isEditContact ? "Edit Contact" : "Add Contact"}</h6>
        </div>
        <div
          style={{ float: "right", cursor: "pointer" }}
          onClick={handleClose}
        >
          <CloseIcon />
        </div>
      </Modal.Header>
      <Modal.Body>
        <div className="form-contents-cs">
          <TextField label="First Name" fullWidth />
          <TextField label="Last Name" fullWidth />
          <TextField label="Email Id" fullWidth />
          <TextField label="Mobile Number" fullWidth />
          <div>
            Inactive
            <Switch
              checked={rowData.status === "active"}
              onChange={() => onStatusChange(rowData)}
            />
            Active
          </div>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onAddContactClick}>
          {isEditContact ? "Update Contact" : "Add Contact"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddContactModal;
